import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import "../styles/dashboard.css";
import AddUpdateProductModal from "./AddUpdateProductModal";


export default function Inventory() {
  // ===============================
  // STATE
  // ===============================
  const [items, setItems] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [showModal, setShowModal] = useState(false);


  const [product, setProduct] = useState({
    sku: "",
    productName: "",
    unitPrice: "",
    quantity: "",
    reorderLevel: ""
  });

  const role = localStorage.getItem("role");
  const token = localStorage.getItem("token");
  const nav = useNavigate();

  // ===============================
  // AXIOS INSTANCE (JWT)
  // ===============================
  const api = axios.create({
    baseURL: "http://localhost:8080",
    headers: {
      Authorization: `Bearer ${token}`
    }
  });

  // ===============================
  // LOAD INVENTORY
  // ===============================
  const loadInventory = async () => {
    try {
      const res = await api.get("/api/inventory/all");
      setItems(res.data || []);
    } catch (err) {
      alert("Session expired. Please login again.");
      localStorage.clear();
      nav("/");
    }
  };

  useEffect(() => {
    loadInventory();
  }, []);

  // ===============================
  // ADD / UPDATE PRODUCT (ADMIN)
  // ===============================
  const saveProduct = async () => {
    if (role !== "ADMIN") {
      alert("Admin only");
      return;
    }

    if (!product.sku || !product.productName) {
      alert("SKU and Product Name are required");
      return;
    }

    try {
      await api.post("/api/inventory/update", {
        sku: product.sku,
        productName: product.productName,
        unitPrice: Number(product.unitPrice),
        quantity: Number(product.quantity),
        reorderLevel: Number(product.reorderLevel)
      });

      setShowForm(false);
      setProduct({
        sku: "",
        productName: "",
        unitPrice: "",
        quantity: "",
        reorderLevel: ""
      });

      loadInventory();
    } catch (err) {
      alert("Failed to save product");
    }
  };

  // ===============================
  // LOGOUT
  // ===============================
  const logout = () => {
    localStorage.clear();
    nav("/");
  };
{role === "ADMIN" && (
  <button
    className="btn btn-primary"
    style={{ marginBottom: 15 }}
    onClick={() => setShowModal(true)}
  >
    + Add / Update Product
  </button>
)}

  // ===============================
  // UI
  // ===============================
  return (
    <>
      {/* TOP BAR */}
      <div className="topbar">
        <span>Inventra Inventory System</span>
        <span>{role}</span>
      </div>

      <div className="dashboard-container">
        {/* SIDEBAR */}
        <div className="sidebar">
          <a className="active">Inventory</a>
          <a className="logout" onClick={logout}>Logout</a>
        </div>

        {/* MAIN */}
        <div className="main">
          <h2 className="page-title">Inventory</h2>

          {/* SUMMARY CARDS */}
          <div className="cards">
            <div className="card blue">
              <h3>Total Products</h3>
              <p>{items.length}</p>
            </div>

            <div className="card green">
              <h3>In Stock</h3>
              <p>{items.filter(p => p.quantity > p.reorderLevel).length}</p>
            </div>

            <div className="card red">
              <h3>Low Stock</h3>
              <p>{items.filter(p => p.quantity <= p.reorderLevel).length}</p>
            </div>
          </div>

          {/* ADMIN BUTTON */}
          {role === "ADMIN" && (
            <button
              className="btn btn-primary"
              style={{ marginBottom: "20px" }}
              onClick={() => setShowForm(true)}
            >
              ➕ Add / Update Product
            </button>
          )}

          {/* PRODUCT FORM */}
          {role === "ADMIN" && showForm && (
            <div className="form-box">
              <h3>Add / Update Product</h3>

              <input
                placeholder="SKU"
                value={product.sku}
                onChange={e => setProduct({ ...product, sku: e.target.value })}
              />

              <input
                placeholder="Product Name"
                value={product.productName}
                onChange={e => setProduct({ ...product, productName: e.target.value })}
              />

              <input
                type="number"
                placeholder="Unit Price"
                value={product.unitPrice}
                onChange={e => setProduct({ ...product, unitPrice: e.target.value })}
              />

              <input
                type="number"
                placeholder="Quantity"
                value={product.quantity}
                onChange={e => setProduct({ ...product, quantity: e.target.value })}
              />

              <input
                type="number"
                placeholder="Low Stock Limit"
                value={product.reorderLevel}
                onChange={e => setProduct({ ...product, reorderLevel: e.target.value })}
              />

              <button className="btn btn-primary" onClick={saveProduct}>
                Save
              </button>

              <button
                className="btn"
                style={{ marginLeft: "10px" }}
                onClick={() => setShowForm(false)}
              >
                Cancel
              </button>
            </div>
          )}

          {/* INVENTORY TABLE */}
          <table className="product-table">
            <thead>
              <tr>
                <th>SKU</th>
                <th>Name</th>
                <th>Quantity</th>
                <th>Price</th>
                <th>Status</th>
              </tr>
            </thead>

            <tbody>
              {items.map(p => (
                <tr
                  key={p.id}
                  style={{ cursor: "pointer" }}
                  onClick={() => nav(`/inventory/${p.sku}`)}
                >
                  <td>{p.sku}</td>
                  <td>{p.productName}</td>
                  <td>{p.quantity}</td>
                  <td>₹{p.unitPrice}</td>
                  <td>
                    {p.quantity <= p.reorderLevel ? (
                      <span className="low-stock">Low</span>
                    ) : (
                      <span className="in-stock">In Stock</span>
                    )}
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          {items.length === 0 && (
            <p className="empty-msg">No products found</p>
          )}
        </div>
      </div>
    </>
  );

}
