import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Inventory() {
  const [items, setItems] = useState([]);
  const [i, setI] = useState({ name: "", quantity: 0, price: 0 });
  const role = localStorage.getItem("role");
  const nav = useNavigate();

  const api = axios.create({
    baseURL: "http://localhost:8080/api/inventory"

  });

  const load = async () => {
    const res = await api.get("/all");
    setItems(res.data);
  };

  const add = async () => {
    if (role !== "ADMIN") return alert("Admin only");
    await api.post("/add", i);
    setI({ name: "", quantity: 0, price: 0 });
    load();
  };

  const del = async (id) => {
    if (role !== "ADMIN") return alert("Admin only");
    await api.delete("/delete/" + id);
    load();
  };

  const logout = () => {
    localStorage.clear();
    nav("/");
  };

  useEffect(() => {
    load();
  }, []);

  return (
    <>
      {/* TOP BAR */}
      <div className="topbar">
        <span>Inventra Inventory System</span>
        <span>{role}</span>
      </div>

      <div className="dashboard-container">
        {/* SIDEBAR */}
        <div className="sidebar">
          <a className="active">Dashboard</a>
          <a>Inventory</a>
          <a className="logout" onClick={logout}>Logout</a>
        </div>

        {/* MAIN CONTENT */}
        <div className="main">
          <h2 className="page-title">Inventory Dashboard</h2>

          {/* DASHBOARD CARDS */}
          <div className="cards">
            <div className="card blue">
              <h3>Total Products</h3>
              <p>{items.length}</p>
            </div>

            <div className="card green">
              <h3>In Stock</h3>
              <p>{items.filter(x => x.quantity > 5).length}</p>
            </div>

            <div className="card red">
              <h3>Low Stock</h3>
              <p>{items.filter(x => x.quantity <= 5).length}</p>
            </div>
          </div>

          {/* LOW STOCK ALERT */}
          {items.some(x => x.quantity <= 5) && (
            <p className="alert">âš  Low Stock Alert</p>
          )}

          {/* ADD PRODUCT (ADMIN ONLY) */}
          {role === "ADMIN" && (
            <div style={{ marginBottom: "20px" }}>
              <input
                placeholder="Product Name"
                value={i.name}
                onChange={e => setI({ ...i, name: e.target.value })}
              />
              <input
                type="number"
                placeholder="Quantity"
                value={i.quantity}
                onChange={e => setI({ ...i, quantity: Number(e.target.value) })}
              />
              <input
                type="number"
                placeholder="Price"
                value={i.price}
                onChange={e => setI({ ...i, price: Number(e.target.value) })}
              />
              <button className="btn btn-primary" onClick={add}>
                Add Product
              </button>
            </div>
          )}

          {/* INVENTORY TABLE */}
          <table className="product-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Quantity</th>
                <th>Status</th>
                <th>Price</th>
                {role === "ADMIN" && <th>Action</th>}
              </tr>
            </thead>
            <tbody>
              {items.map(x => (
                <tr key={x.id}>
                  <td>{x.name}</td>
                  <td>{x.quantity}</td>
                  <td>
                    {x.quantity <= 5 ? (
                      <span className="low-stock">Low</span>
                    ) : (
                      <span className="in-stock">In Stock</span>
                    )}
                  </td>
                  <td className="price">{x.price}</td>
                  {role === "ADMIN" && (
                    <td>
                      <button
                        className="btn btn-danger"
                        onClick={() => del(x.id)}
                      >
                        Delete
                      </button>
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>

          {items.length === 0 && (
            <p className="empty-msg">No products available</p>
          )}
        </div>
      </div>
    </>
  );
}
