import {useEffect,useState} from "react";
import axios from "axios";
import {useNavigate} from "react-router-dom";

export default function Inventory(){
 const [items,setItems]=useState([]);
 const [i,setI]=useState({name:"",quantity:0,price:0});
 const role=localStorage.getItem("role");
 const nav=useNavigate();
 const api=axios.create({baseURL:"http://localhost:8080/api/inventory"});

 const load=async()=>setItems((await api.get("/all")).data);
 const add=async()=>{ if(role!=="ADMIN") return alert("Admin only"); await api.post("/add",i); load();};
 const del=async id=>{ if(role!=="ADMIN") return alert("Admin only"); await api.delete("/delete/"+id); load();};
 const logout=()=>{localStorage.clear();nav("/")};

 useEffect(()=>{load()},[]);

 return(<div>
  <h2>Inventory Dashboard ({role})</h2>
  <button onClick={logout}>Logout</button>
  {items.some(x=>x.quantity<5)&&<p className="alert">Low Stock Alert</p>}
  <input placeholder="Name" onChange={e=>setI({...i,name:e.target.value})}/>
  <input placeholder="Qty" onChange={e=>setI({...i,quantity:e.target.value})}/>
  <input placeholder="Price" onChange={e=>setI({...i,price:e.target.value})}/>
  <button onClick={add}>Add</button>
  <table>
   <thead><tr><th>Name</th><th>Qty</th><th>Price</th><th>Action</th></tr></thead>
   <tbody>
    {items.map(x=>(
     <tr key={x.id}>
      <td>{x.name}</td><td>{x.quantity}</td><td>{x.price}</td>
      <td><button onClick={()=>del(x.id)}>Delete</button></td>
     </tr>
    ))}
   </tbody>
  </table>
 </div>);
}
