import { useParams, useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/product.css";

export default function ProductDetail() {
  const { sku } = useParams();
  const nav = useNavigate();
  const token = localStorage.getItem("token");

  const [product, setProduct] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:8080/api/inventory/all", {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then(res => {
      const found = res.data.find(p => p.sku === sku);
      setProduct(found);
    })
    .catch(() => {
      alert("Session expired");
      nav("/");
    });
  }, [sku]);

  if (!product) return <p style={{ padding: 20 }}>Loading...</p>;

  const low = product.quantity <= product.reorderLevel;

  return (
    <div className="product-page">
      <button className="btn back-btn" onClick={() => nav("/inventory")}>
        ← Back to Inventory
      </button>

      <div className="product-card">
        <h2>{product.productName}</h2>

        <p><b>SKU:</b> {product.sku}</p>
        <p><b>Price:</b> ₹{product.unitPrice}</p>
        <p><b>Quantity:</b> {product.quantity}</p>
        <p><b>Low Stock Limit:</b> {product.reorderLevel}</p>

        {low && (
          <div className="alert-box">
            ⚠ Low Stock Alert
          </div>
        )}
      </div>
    </div>
  );
}
