import {useState} from "react";
import axios from "axios";
import {useNavigate} from "react-router-dom";
import ".\src\styles\dashboard.css";


export default function Login(){
 const [u,setU]=useState(""),[p,setP]=useState("");
 const nav=useNavigate();
 const login=async e=>{
  e.preventDefault();
  const r=await axios.post("http://localhost:8080/api/auth/login",{username:u,password:p});
  localStorage.setItem("token",r.data.token);
  localStorage.setItem("role",r.data.role);
  nav("/inventory");
 };
 return(<form onSubmit={login}>
  <h2>Login</h2>
  <input placeholder="Username" onChange={e=>setU(e.target.value)}/>
  <input type="password" placeholder="Password" onChange={e=>setP(e.target.value)}/>
  <button>Login</button>
  <a href="/register">Register</a>
 </form>);
}
