import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Inventory() {
  const [items, setItems] = useState([]);

  // ðŸ”¹ UPDATED STATE (SKU ADDED)
  const [i, setI] = useState({
    sku: "",
    name: "",
    quantity: 0,
    price: 0
  });

  const role = localStorage.getItem("role");
  const nav = useNavigate();

  const api = axios.create({
    baseURL: "http://localhost:8080/api/inventory"
  });

  const load = async () => {
    const res = await api.get("/all");
    setItems(res.data);
  };

  // ðŸ”¹ UPDATED: ADD / UPDATE STOCK (SKU BASED)
  const updateStock = async () => {
    if (role !== "ADMIN") return alert("Admin only");

    if (!i.sku) {
      alert("Product SKU is required");
      return;
    }

    const payload = {
      sku: i.sku,
      productName: i.productName,
      price: i.price,
      quantity: i.quantity,
      reorderLevel: 5
    };

    await api.post("/update", payload);

    // Reset form
    setI({ sku: "", productName: "", quantity: 0, price: 0 });
    load();
  };

  const del = async (id) => {
    if (role !== "ADMIN") return alert("Admin only");
    await api.delete("/delete/" + id);
    load();
  };

  const logout = () => {
    localStorage.clear();
    nav("/");
  };

  useEffect(() => {
    load();
  }, []);

  return (
    <>
      {/* TOP BAR */}
      <div className="topbar">
        <span>Inventra Inventory System</span>
        <span>{role}</span>
      </div>

      <div className="dashboard-container">
        {/* SIDEBAR */}
        <div className="sidebar">
          <a className="active">Dashboard</a>
          <a>Inventory</a>
          <a className="logout" onClick={logout}>Logout</a>
        </div>

        {/* MAIN CONTENT */}
        <div className="main">
          <h2 className="page-title">Inventory Dashboard</h2>

          {/* DASHBOARD CARDS */}
          <div className="cards">
            <div className="card blue">
              <h3>Total Products</h3>
              <p>{items.length}</p>
            </div>

            <div className="card green">
              <h3>In Stock</h3>
              <p>{items.filter(x => x.quantity > 5).length}</p>
            </div>

            <div className="card red">
              <h3>Low Stock</h3>
              <p>{items.filter(x => x.quantity <= 5).length}</p>
            </div>
          </div>

          {/* LOW STOCK ALERT */}
          {items.some(x => x.quantity <= 5) && (
            <p className="alert">âš  Low Stock Alert</p>
          )}

          {/* ADD / UPDATE STOCK (ADMIN ONLY) */}
          {role === "ADMIN" && (
            <div style={{ marginBottom: "20px" }}>
              <input
                placeholder="Product SKU (e.g. SKU-101)"
                value={i.sku}
                onChange={e => setI({ ...i, sku: e.target.value })}
              />

              <input
                placeholder="Product Name"
                value={i.productName}
                onChange={e => setI({ ...i, productName: e.target.value })}
              />

              <input
                type="number"
                placeholder="Quantity (+IN / -OUT)"
                value={i.quantity}
                onChange={e => setI({ ...i, quantity: Number(e.target.value) })}
              />

              <input
                type="number"
                placeholder="Unit Price"
                value={i.price}
                onChange={e => setI({ ...i, price: Number(e.target.value) })}
              />

              <button className="btn btn-primary" onClick={updateStock}>
                Add / Update Stock
              </button>

              <p style={{ fontSize: "12px", marginTop: "5px" }}>
                Use <b>positive</b> quantity to add stock, <b>negative</b> to remove stock
              </p>
            </div>
          )}

          {/* INVENTORY TABLE */}
          <table className="product-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Quantity</th>
                <th>Status</th>
                <th>Price</th>
                {role === "ADMIN" && <th>Action</th>}
              </tr>
            </thead>
            <tbody>
              {items.map(x => (
                <tr key={x.id}>
                  <td>{x.productName}</td>
                  <td>{x.quantity}</td>
                  <td>
                    {x.quantity <= 5 ? (
                      <span className="low-stock">Low</span>
                    ) : (
                      <span className="in-stock">In Stock</span>
                    )}
                  </td>
                  <td className="price">{x.price}</td>
                  {role === "ADMIN" && (
                    <td>
                      <button
                        className="btn btn-danger"
                        onClick={() => del(x.id)}
                      >
                        Delete
                      </button>
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>

          {items.length === 0 && (
            <p className="empty-msg">No products available</p>
          )}
        </div>
      </div>
    </>
  );
}
