import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Inventory() {

  // ===============================
  // STATE
  // ===============================
  const [items, setItems] = useState([]);

  const [newProduct, setNewProduct] = useState({
    sku: "",
    productName: "",
    unitPrice: "",
    quantity: "",
    reorderLevel: ""
  });

  const [stockUpdate, setStockUpdate] = useState({
    sku: "",
    quantity: ""
  });

  const role = localStorage.getItem("role");
  const token = localStorage.getItem("token");
  const nav = useNavigate();

  // ===============================
  // AXIOS WITH JWT + AUTO LOGOUT
  // ===============================
  const api = axios.create({
    baseURL: "http://localhost:8080/api/inventory",
    headers: {
      Authorization: `Bearer ${token}`
    }
  });

  api.interceptors.response.use(
    res => res,
    err => {
      if (err.response && err.response.status === 401) {
        localStorage.clear();
        alert("Session expired. Please login again.");
        nav("/");
      }
      return Promise.reject(err);
    }
  );

  // ===============================
  // LOAD INVENTORY
  // ===============================
  const load = async () => {
    const res = await api.get("/all");
    setItems(res.data);
  };

  useEffect(() => {
    if (!token) nav("/");
    load();
  }, []);

  // ===============================
  // ADD PRODUCT (ADMIN)
  // ===============================
  const addProduct = async () => {
    if (role !== "ADMIN") return alert("Admin only");

    if (
      !newProduct.sku ||
      !newProduct.productName ||
      !newProduct.unitPrice ||
      !newProduct.quantity ||
      !newProduct.reorderLevel
    ) {
      alert("All fields are required");
      return;
    }

    if (!window.confirm("Add / Update this product?")) return;

    await api.post("/update", {
      sku: newProduct.sku,
      productName: newProduct.productName,
      unitPrice: Number(newProduct.unitPrice),
      quantity: Number(newProduct.quantity),
      reorderLevel: Number(newProduct.reorderLevel)
    });

    setNewProduct({
      sku: "",
      productName: "",
      unitPrice: "",
      quantity: "",
      reorderLevel: ""
    });

    load();
  };

  // ===============================
  // UPDATE STOCK (ADMIN)
  // ===============================
  const updateStock = async () => {
    if (role !== "ADMIN") return alert("Admin only");

    if (!stockUpdate.sku || stockUpdate.quantity === "") {
      alert("SKU and quantity required");
      return;
    }

    if (!window.confirm("Update stock?")) return;

    await api.post("/update", {
      sku: stockUpdate.sku,
      quantity: Number(stockUpdate.quantity)
    });

    setStockUpdate({ sku: "", quantity: "" });
    load();
  };

  // ===============================
  // DELETE PRODUCT
  // ===============================
  const del = async (id) => {
    if (role !== "ADMIN") return alert("Admin only");

    if (!window.confirm("Delete this product permanently?")) return;

    await api.delete("/delete/" + id);
    load();
  };

  const logout = () => {
    localStorage.clear();
    nav("/");
  };

  // ===============================
  // UI
  // ===============================
  return (
    <>
      {/* TOP BAR */}
      <div className="topbar">
        <span>Inventra Inventory System</span>
        <span>{role}</span>
      </div>

      <div className="dashboard-container">

        {/* SIDEBAR */}
        <div className="sidebar">
          <a className="active">Inventory</a>
          <a className="logout" onClick={logout}>Logout</a>
        </div>

        {/* MAIN */}
        <div className="main">
          <h2 className="page-title">Inventory Dashboard</h2>

          {/* DASHBOARD CARDS */}
          <div className="cards">
            <div className="card blue">
              <h3>Total Products</h3>
              <p>{items.length}</p>
            </div>

            <div className="card green">
              <h3>In Stock</h3>
              <p>{items.filter(x => x.quantity > x.reorderLevel).length}</p>
            </div>

            <div className="card red">
              <h3>Low Stock</h3>
              <p>{items.filter(x => x.quantity <= x.reorderLevel).length}</p>
            </div>
          </div>

          {/* ADMIN FORMS */}
          {role === "ADMIN" && (
            <>
              {/* ADD PRODUCT */}
              <div className="form-box">
                <h3>Add / Update Product</h3>

                <input placeholder="SKU"
                  value={newProduct.sku}
                  onChange={e => setNewProduct({ ...newProduct, sku: e.target.value })}
                />

                <input placeholder="Product Name"
                  value={newProduct.productName}
                  onChange={e => setNewProduct({ ...newProduct, productName: e.target.value })}
                />

                <input type="number" placeholder="Unit Price"
                  value={newProduct.unitPrice}
                  onChange={e => setNewProduct({ ...newProduct, unitPrice: e.target.value })}
                />

                <input type="number" placeholder="Quantity (+ / -)"
                  value={newProduct.quantity}
                  onChange={e => setNewProduct({ ...newProduct, quantity: e.target.value })}
                />

                <input type="number" placeholder="Low Stock Limit"
                  value={newProduct.reorderLevel}
                  onChange={e => setNewProduct({ ...newProduct, reorderLevel: e.target.value })}
                />

                <button className="btn btn-primary" onClick={addProduct}>
                  Save Product
                </button>
              </div>

              {/* UPDATE STOCK */}
              <div className="form-box">
                <h3>Update Stock</h3>

                <input placeholder="Product SKU"
                  value={stockUpdate.sku}
                  onChange={e => setStockUpdate({ ...stockUpdate, sku: e.target.value })}
                />

                <input type="number" placeholder="Quantity (+ / -)"
                  value={stockUpdate.quantity}
                  onChange={e => setStockUpdate({ ...stockUpdate, quantity: e.target.value })}
                />

                <button className="btn btn-secondary" onClick={updateStock}>
                  Update Stock
                </button>
              </div>
            </>
          )}

          {/* INVENTORY TABLE */}
          <table className="product-table">
            <thead>
              <tr>
                <th>SKU</th>
                <th>Name</th>
                <th>Qty</th>
                <th>Price</th>
                <th>Status</th>
                {role === "ADMIN" && <th>Action</th>}
              </tr>
            </thead>

            <tbody>
              {items.map(x => (
                <tr
                  key={x.id}
                  className="row-click"
                  onClick={() => {
                    if (role === "ADMIN") {
                      setNewProduct({
                        sku: x.sku,
                        productName: x.productName,
                        unitPrice: x.unitPrice,
                        quantity: "",
                        reorderLevel: x.reorderLevel
                      });
                    }
                  }}
                >
                  <td>{x.sku}</td>

                  <td
                    className="link"
                    onClick={(e) => {
                      e.stopPropagation();
                      nav(`/product/${x.sku}`);
                    }}
                  >
                    {x.productName}
                  </td>

                  <td>{x.quantity}</td>
                  <td>â‚¹{x.unitPrice}</td>

                  <td>
                    {x.quantity <= x.reorderLevel
                      ? <span className="low-stock">Low</span>
                      : <span className="in-stock">In Stock</span>}
                  </td>

                  {role === "ADMIN" && (
                    <td>
                      <button
                        className="btn btn-danger"
                        onClick={(e) => {
                          e.stopPropagation();
                          del(x.id);
                        }}
                      >
                        Delete
                      </button>
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>

          {items.length === 0 && (
            <p className="empty-msg">No products available</p>
          )}
        </div>
      </div>
    </>
  );
}
