import { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Inventory() {
  const [items, setItems] = useState([]);

  // ✅ MATCHES BACKEND PRODUCT MODEL
  const [i, setI] = useState({
    sku: "",
    productName: "",
    quantity: 0,
    unitPrice: 0,
    reorderLevel: 5
  });

  const role = localStorage.getItem("role");
  const nav = useNavigate();

  const api = axios.create({
    baseURL: "http://localhost:8080/api/inventory"
  });

  const load = async () => {
    const res = await api.get("/all");
    setItems(res.data);
  };

  // ===============================
  // ADD / UPDATE PRODUCT (ADMIN)
  // ===============================
  const updateStock = async () => {
    if (role !== "ADMIN") {
      alert("Admin only");
      return;
    }

    if (!i.sku || !i.productName) {
      alert("SKU and Product Name are required");
      return;
    }

    const payload = {
      sku: i.sku,
      productName: i.productName,
      unitPrice: i.unitPrice,
      quantity: i.quantity,
      reorderLevel: i.reorderLevel
    };

    await api.post("/update", payload);

    setI({
      sku: "",
      productName: "",
      quantity: "",
      unitPrice: 0,
      reorderLevel: 5
    });

    load();
  };

  // ===============================
  // DELETE PRODUCT (ADMIN)
  // ===============================
  const del = async (id) => {
    if (role !== "ADMIN") {
      alert("Admin only");
      return;
    }
    await api.delete("/delete/" + id);
    load();
  };

  const logout = () => {
    localStorage.clear();
    nav("/");
  };

  useEffect(() => {
    load();
  }, []);

  return (
    <>
      {/* TOP BAR */}
      <div className="topbar">
        <span>Inventra Inventory System</span>
        <span>{role}</span>
      </div>

      <div className="dashboard-container">
        {/* SIDEBAR */}
        <div className="sidebar">
          <a className="active">Inventory</a>
          <a className="logout" onClick={logout}>Logout</a>
        </div>

        {/* MAIN CONTENT */}
        <div className="main">
          <h2 className="page-title">Inventory</h2>

          {/* DASHBOARD CARDS */}
          <div className="cards">
            <div className="card blue">
              <h3>Total Products</h3>
              <p>{items.length}</p>
            </div>

            <div className="card green">
              <h3>In Stock</h3>
              <p>{items.filter(x => x.quantity > x.reorderLevel).length}</p>
            </div>

            <div className="card red">
              <h3>Low Stock</h3>
              <p>{items.filter(x => x.quantity <= x.reorderLevel).length}</p>
            </div>
          </div>

          {/* LOW STOCK ALERT */}
          {items.some(x => x.quantity <= x.reorderLevel) && (
            <p className="alert">⚠ Low Stock Alert</p>
          )}

          {/* ADMIN FORM */}
          {role === "ADMIN" && (
            <div className="form-box">
              <input
                placeholder="Product SKU"
                value={i.sku}
                onChange={e => setI({ ...i, sku: e.target.value })}
              />

              <input
                placeholder="Product Name"
                value={i.productName}
                onChange={e => setI({ ...i, productName: e.target.value })}
              />

              <input
                type="number"
                placeholder="Unit Price"
                value={i.unitPrice}
                onChange={e => setI({ ...i, unitPrice: Number(e.target.value) })}
              />

              <input
                type="number"
                placeholder="Quantity (+IN / -OUT)"
                value={i.quantity}
                onChange={e => setI({ ...i, quantity: Number(e.target.value) })}
              />

              <input
                type="number"
                placeholder="Low Stock Limit"
                value={i.reorderLevel}
                onChange={e => setI({ ...i, reorderLevel: Number(e.target.value) })}
              />

              <button className="btn btn-primary" onClick={updateStock}>
                Add / Update Product
              </button>
            </div>
          )}

          {/* INVENTORY TABLE */}
          <table className="product-table">
            <thead>
              <tr>
                <th>SKU</th>
                <th>Name</th>
                <th>Quantity</th>
                <th>Price</th>
                <th>Status</th>
                {role === "ADMIN" && <th>Action</th>}
              </tr>
            </thead>

            <tbody>
              {items.map(x => (
                <tr key={x.id}>
                  <td>{x.sku}</td>
                  <td>{x.productName}</td>
                  <td>{x.quantity}</td>
                  <td>₹{x.unitPrice}</td>
                  <td>
                    {x.quantity <= x.reorderLevel ? (
                      <span className="low-stock">Low</span>
                    ) : (
                      <span className="in-stock">In Stock</span>
                    )}
                  </td>
                  {role === "ADMIN" && (
                    <td>
                      <button
                        className="btn btn-danger"
                        onClick={() => del(x.id)}
                      >
                        Delete
                      </button>
                    </td>
                  )}
                </tr>
              ))}
            </tbody>
          </table>

          {items.length === 0 && (
            <p className="empty-msg">No products available</p>
          )}
        </div>
      </div>
    </>
  );
}
