import { useState } from "react";
import axios from "axios";

export default function AddUpdateProductModal({ onClose }) {
  const [p, setP] = useState({
    sku: "",
    productName: "",
    unitPrice: "",
    quantity: "",
    reorderLevel: ""
  });

  const api = axios.create({
    baseURL: "http://localhost:8080/api/inventory",
    headers: {
      Authorization: "Bearer " + localStorage.getItem("token")
    }
  });

  const submit = async () => {
    await api.post("/update", p);
    onClose();
    window.location.reload();
  };

  return (
    <div className="modal-backdrop">
      <div className="modal">
        <h3>Add / Update Product</h3>

        <input placeholder="SKU" onChange={e => setP({...p, sku: e.target.value})} />
        <input placeholder="Product Name" onChange={e => setP({...p, productName: e.target.value})} />
        <input type="number" placeholder="Latest Price" onChange={e => setP({...p, unitPrice: e.target.value})} />
        <input type="number" placeholder="Quantity (+ / -)" onChange={e => setP({...p, quantity: e.target.value})} />
        <input type="number" placeholder="Low Stock Limit" onChange={e => setP({...p, reorderLevel: e.target.value})} />

        <div className="modal-actions">
          <button className="btn primary" onClick={submit}>Save</button>
          <button className="btn" onClick={onClose}>Cancel</button>
        </div>
      </div>
    </div>
  );
}
