import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";
import "../styles/product.css";

export default function ProductDetails() {
  const { sku } = useParams();
  const [product, setProduct] = useState(null);

  useEffect(() => {
    axios.get("http://localhost:8080/api/inventory/all")
      .then(res => {
        const found = res.data.find(p => p.sku === sku);
        setProduct(found);
      });
  }, [sku]);

  if (!product) return <p>Loading...</p>;

  const isLow = product.quantity <= product.reorderLevel;

  return (
    <div className="product-page">
      <h2>{product.productName}</h2>

      <div className="product-card">
        <p><b>Product ID:</b> {product.sku}</p>
        <p><b>Price:</b> ₹{product.unitPrice}</p>
        <p><b>Stock:</b> {product.quantity}</p>

        {isLow && (
          <div className="alert-box">
            ⚠ Low Stock Alert
          </div>
        )}
      </div>
    </div>
  );
}
